SELECT
sph.sy_from AS SY_FROM,
sph.school_id AS SCHOOL_ID,
IFNULL(SUM((SELECT SUM(IFNULL(pps.tot_plantilla_items,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_plantilla',
IFNULL(SUM((SELECT SUM(IFNULL(pps.actual_male,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_actual_male',
IFNULL(SUM((SELECT SUM(IFNULL(pps.actual_female,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_actual_female',
IFNULL(SUM((SELECT SUM(IFNULL(pps.actual_male,0)+IFNULL(pps.actual_female,0))   AS qty FROM ebeisdb.personnel_position_summary pps WHERE pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4total_actualmf',
IFNULL(SUM((SELECT SUM(IFNULL(pps.tot_onleave,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_onleave',
