IFNULL(SUM((SELECT SUM(IFNULL(pps.detailed_admin_teachers,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_detailed_office',
IFNULL(SUM((SELECT SUM(IFNULL(pps.detailed_within_office,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_detailed_within',
IFNULL(SUM((SELECT SUM(IFNULL(pps.detailed_outside_office,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_detailed_outside',
IFNULL(SUM((SELECT SUM(IFNULL(pps.detailed_school,0))  AS qty FROM ebeisdb.personnel_position_summary pps WHERE  pps.position_id = 131 and pps.report_history_id = rh.id)),0) as 'MT4_detailed_school',
